#include<algorithm>#include<vector>#include<list>#include<iterator>#include<iostream>using namespace std;//function that prints the passed argumentvoid print(int elem){    cout << elem << " ";}int sequare(int value){    return value * value;}int main(){    vector<int> coll = {1, 2, 3, 4, 5};    list<int> coll2;    for_each(coll.cbegin(), coll.cend(), print);    cout << endl;    //transform each element from coll to coll2    //square transformed values    transform(coll.cbegin(), coll.cend(),   //soource        back_inserter(coll2),         sequare);    cout << "coll2: " << endl;    for (int i : coll2)    {        cout << i << " ";    }    cout << endl;        system("pause");    return EXIT_SUCCESS;}