#include<iostream>#include<vector>#include<list>#include<string>using namespace std;template<typename T>inline void print(const T& coll, const string& info = ""){    cout << info;    for (const auto& elem : coll)    {        cout << elem << " ";    }    cout << endl;}template<typename T>inline void print_oldStyle(const T& coll, const string& info = ""){    cout << info;    typename T::const_iterator it;    for (it = coll.begin(); it != coll.end();++it)    {        cout << *it << " ";    }    cout << endl;}int main(){    vector<int> coll = {1, 2, 3, 4, 5, 6, 7, 8, 9};    list<int> coll2(coll.begin(), coll.end());    print(coll, "vector: ");    print_oldStyle(coll2, "list: ");        system("pause");    return EXIT_SUCCESS;}